    cortex_ingester_tsdb_exemplar_series_with_exemplars_in_storage{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"} unless on (job)
    cortex_partition_ring_partitions{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"}
  )
  / on (cluster, namespace) group_left()
  max by (cluster, namespace) (cortex_distributor_replication_factor{cluster=~"$cluster", job=~"($namespace)/((distributor.*|cortex|mimir|mimir-write.*))"})
)
or
( # Ingest storage
  sum by (cluster, namespace, user) (
    max by (ingester_id, cluster, namespace, user) (
      label_replace(
        cortex_ingester_tsdb_exemplar_series_with_exemplars_in_storage{cluster=~"$cluster", job=~"($namespace)/((ingester.*|cortex|mimir|mimir-write.*))"},
        "ingester_id", "$1", "pod", ".*-([0-9]+)$"
      )
    )
  )
)
)

